mapper.update:
UPDATE QUIZ_SCORE
SET
  QUIZ_DT = now()

    ,SCORE = #{teamScore}


    ,CHANCE_1 = #{chance1}


    ,CHANCE_2 = #{chance1}


    ,CHANCE_3 = #{chance1}


    ,CHANCE_4 = #{chance1}


    ,CHANCE_5 = #{chance1}


    ,CHANCE_6 = #{chance1}

    ,SCORE_END = 1
    ,SCORE_END_DT = now()
WHERE
  TEAM_SEQ = #{teamSeq}
  AND QUIZ_NUM = #{quizNum}
  AND SCORE_END IS NOT TRUE

mapper.insert:
INSERT INTO QUIZ_CHANCE
(
  QUIZ_NUM,
  TEAM_SEQ,
  CHANCE_TYPE,
  TARGET_TEAM,
  AGREE_CHANCE,
  REG_DT
)
VALUES
(
  (SELECT QUIZ_NUM+1 FROM QUIZ_PROCESS WHERE NUM = 1)
  ,#{teamSeq}
  ,#{quizChance}
  ,#{targetTeam}
  ,#{agreeChance}
  ,now()
)
