mapper.select:
SELECT TI.*,IFNULL(TI.PROCESS_QUIZ_NUM_ORG,0) AS PROCESS_QUIZ_NUM, IFNULL(TI.BASE_SCORE,0) + IFNULL(TI.ANSWER_SCORE,0) AS TOTAL_SCORE FROM ( SELECT T.* , P.*, (SELECT CHANCE_TYPE FROM QUIZ_CHANCE C WHERE T.SEQ = C.TEAM_SEQ AND C.QUIZ_NUM = P.QUIZ_NUM) AS CHANCE_TYPE, (SELECT CHANCE_TYPE FROM QUIZ_CHANCE C WHERE T.SEQ = C.TEAM_SEQ AND C.QUIZ_NUM = P.QUIZ_NUM+1) AS REQUEST_CHANCE_TYPE, (SELECT QUIZ_ANSWER FROM QUIZ_SCORE S WHERE T.SEQ = S.TEAM_SEQ AND S.QUIZ_NUM = P.QUIZ_NUM) AS QUIZ_ANSWER, (SELECT IFNULL(SUM(SCORE),0) FROM QUIZ_SCORE QS WHERE T.SEQ = QS.TEAM_SEQ GROUP BY TEAM_SEQ) AS ANSWER_SCORE, (SELECT NUM FROM QUIZ_INFO WHERE PROCESS_YN = 1) AS PROCESS_QUIZ_NUM_ORG, (SELECT QUIZ_TYPE FROM QUIZ_INFO WHERE PROCESS_YN = 1) AS QUIZ_TYPE FROM team_info T, (SELECT QUIZ_NUM, QUIZ_END FROM QUIZ_PROCESS WHERE NUM=1) P ) TI
WHERE TI.SEQ = #{teamSeq}
ORDER BY TI.SCHOOL_NAME

select * from TEAM_INFO


    WHERE SEQ = #{teamSeq}


ORDER BY TEAM_CODE, SCHOOL_NAME, NAME

mapper.update:
UPDATE TEAM_INFO SET



        CHANCE_1 = #{chanceValue}




        CHANCE_2 = #{chanceValue}




        CHANCE_3 = #{chanceValue}




        CHANCE_4 = #{chanceValue}




        CHANCE_5 = #{chanceValue}




        CHANCE_6 = #{chanceValue}



        1 = 1


WHERE SEQ = #{teamSeq}
